#pragma GCC optimize("Ofast")#pragma GCC optimize("inline")#pragma GCC optimize("omit-frame-pointer")#pragma GCC optimize("unroll-loops")#include <bits/stdc++.h>using namespace std;int n;vector<int> a, b;long long sub1(vector<int> v) {    long long res = 0;    for (int _ = 1; _ <= v.size(); ++_)    for (int i = 0; i < (int)v.size()-1; ++i) {        if (v[i] > v[i+1]) {            res++;            swap(v[i],v[i+1]);        }    }    return res;}long long brute_force() {    long long res = 0;    for (auto v = a; v <= b;) {        res += sub1(v);        if (!next_permutation(v.begin(),v.end())) {            return res;        }    }    return res;}int main() {    ios_base::sync_with_stdio(0); cin.tie(0);    freopen("PERMEOW.INP", "r", stdin);    freopen("PERMEOW.OUT", "w", stdout);    cin >> n;    for (int i = 1; i <= n; ++i) {        int x; cin >> x;        a.push_back(x);    }    for (int i = 1; i <= n; ++i) {        int x; cin >> x;        b.push_back(x);    }    if (a==b) {        cout << sub1(a);    }    else {        cout << brute_force();    }    return 0;}